use apartments\classes\Apartment;

add_shortcode('apartmentArchive', 'apartmentArchive');
require_once INC_DIR . '/apartments/classes/Apartment.php';
function apartmentArchive(): string {
	global $wpdb;
	$apartments = get_posts([
		'post_type' => 'boliger',
		'post_status' => 'publish',
		'posts_per_page' => -1
	]);
	$lang = $_POST['lang'] ?? 'da';
	$apartmentsArray = [];
	foreach ($apartments as $apartment) {
		$apartment = new Apartment($apartment->ID);
		if($apartment->getLang() !== $lang){
			continue;
		}
        $apartmentsArray[] = $apartment;
    }
    usort($apartmentsArray, function($a, $b) {
        return $a->getNumber() <=> $b->getNumber();
    });
    ob_start();
    ?>
    <div id="archive">
